select
,
from activity where activityId = #{activityid,jdbcType=INTEGER}

	SELECT * FROM activity



	ORDER BY activityDate DESC


        	 LIMIT #{begin} , #{total};

SELECT a.* ,ac.websiteType FROM activityrecommend ac , activity a  


		AND ac.branchId = #{branchid}



		AND ac.activityId = #{activityid}



		AND ac.websiteType = #{websitetype}



		AND a.activityId = ac.activityId

SELECT sum(a.registeryFee) totalIncome,sum(a.activityprice) totalexl from activity a,activitytype b,activityregistery c,branch d
a.activityTypeID = b.activityTypeID and a.branchId = d.branchId and c.activityId = a.activityId and c.registery = '报名' and b.activityTypeID=1 and d.employeeId = #{employeeid,jdbcType=INTEGER}
and a.activitydate BETWEEN #{start} AND #{end}

SELECT sum(a.registeryFee) totalIncome,sum(a.activityprice) totalexl from activity a,activitytype b,activityregistery c,branch d
a.activityTypeID = b.activityTypeID and a.branchId = d.branchId and c.activityId = a.activityId and c.registery = '报名' and b.activityTypeID=2 and d.employeeId = #{employeeid,jdbcType=INTEGER}
and a.activitydate BETWEEN #{start} AND #{end}
